/* eslint-disable no-unused-vars */
import React, { useEffect, useState } from 'react';
import {
  Alert,
  Box,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Button,
  TextField,
} from '@mui/material';

import Layout from '../../components/Layout/Layout';
import PageHeader from '../../components/Common/PageHeader';
import DateRangeFilter from '../../components/Common/DateRangeFilter';
import DataTable from '../../components/Common/DataTable';
import LoadingSpinner from '../../components/Common/LoadingSpinner';
import estoqueService from '../../services/estoqueService';
import { formatDateTime } from '../../utils/formatters';

const Estoque = () => {
  const [movimentacoes, setMovimentacoes] = useState([]);
  const [produtoFilter, setProdutoFilter] = useState('');
  const [tipoFilter, setTipoFilter] = useState('');
  const [fromDate, setFromDate] = useState('');
  const [toDate, setToDate] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const load = async (params = {}) => {
      try {
        const data = await estoqueService.listarMovimentacoes(params);
        setMovimentacoes(data || []);
      } catch (err) {
        setError('Erro ao carregar movimentações de estoque');
      } finally {
        setLoading(false);
      }
    };

    load();
  }, []);

  const columns = [
    {
      field: 'dataMovimentacao',
      headerName: 'Data',
      render: (value) => (value ? formatDateTime(value) : '-'),
    },
    { field: 'produtoCodigo', headerName: 'Código' },
    { field: 'produtoNome', headerName: 'Produto' },
    { field: 'tipo', headerName: 'Tipo' },
    { field: 'quantidade', headerName: 'Quantidade', align: 'right' },
    { field: 'motivo', headerName: 'Motivo' },
    { field: 'usuarioNome', headerName: 'Usuário' },
  ];

  if (loading) {
    return (
      <Layout>
        <LoadingSpinner />
      </Layout>
    );
  }

  return (
    <Layout>
      <PageHeader
        title="Movimentações de Estoque"
        subtitle="Entradas e saídas de estoque registradas automaticamente pelas vendas."
      />

      {error && (
        <Alert severity="error" onClose={() => setError('')} sx={{ mb: 2 }}>
          {error}
        </Alert>
      )}

      <Box sx={{ mb: 2, display: 'flex', gap: 2, flexWrap: 'wrap', alignItems: 'center' }}>
        <DateRangeFilter
          from={fromDate}
          to={toDate}
          onChangeFrom={setFromDate}
          onChangeTo={setToDate}
          onApply={() => applyFilters()}
          onClear={() => { setFromDate(''); setToDate(''); }}
        />

        <TextField size="small" label="Produto (nome/código)" value={produtoFilter} onChange={(e) => setProdutoFilter(e.target.value)} />

        <FormControl size="small" sx={{ minWidth: 160 }}>
          <InputLabel>Tipo</InputLabel>
          <Select value={tipoFilter} label="Tipo" onChange={(e) => setTipoFilter(e.target.value)}>
            <MenuItem value="">Todos</MenuItem>
            <MenuItem value="ENTRADA">Entrada</MenuItem>
            <MenuItem value="SAIDA">Saída</MenuItem>
          </Select>
        </FormControl>

        <Button variant="contained" size="small" onClick={() => applyFilters()}>Aplicar</Button>
        <Button variant="outlined" size="small" onClick={() => { setProdutoFilter(''); setTipoFilter(''); setFromDate(''); setToDate(''); applyFilters(); }}>Limpar</Button>
      </Box>

      <DataTable
        columns={columns}
        data={movimentacoes}
        actions={false}
      />
    </Layout>
  );
};

export default Estoque;
