/* eslint-disable no-unused-vars */
import React, { useEffect, useState } from 'react';
import {
  Box,
  Typography,
  Alert,
  Chip,
} from '@mui/material';

import Layout from '../../components/Layout/Layout';
import DataTable from '../../components/Common/DataTable';
import LoadingSpinner from '../../components/Common/LoadingSpinner';
import financeiroService from '../../services/financeiroService';
import { formatCurrency, formatDate } from '../../utils/formatters';

const Financeiro = () => {
  const [lancamentos, setLancamentos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const load = async () => {
      try {
        const data = await financeiroService.listarLancamentos();
        setLancamentos(data || []);
      } catch (err) {
        setError('Erro ao carregar lançamentos financeiros');
      } finally {
        setLoading(false);
      }
    };

    load();
  }, []);

  const columns = [
    { field: 'tipo', headerName: 'Tipo' },
    { field: 'categoria', headerName: 'Categoria' },
    { field: 'descricao', headerName: 'Descrição' },
    {
      field: 'valor',
      headerName: 'Valor',
      align: 'right',
      render: (value) => (value != null ? formatCurrency(value) : '-'),
    },
    {
      field: 'dataVencimento',
      headerName: 'Vencimento',
      render: (value) => (value ? formatDate(value) : '-'),
    },
    {
      field: 'dataPagamento',
      headerName: 'Pagamento',
      render: (value) => (value ? formatDate(value) : '-'),
    },
    {
      field: 'status',
      headerName: 'Status',
      render: (value) => (
        <Chip
          label={value}
          size="small"
          color={value === 'PAGO' ? 'success' : value === 'PENDENTE' ? 'warning' : 'default'}
        />
      ),
    },
    { field: 'formaPagamento', headerName: 'Forma Pagamento' },
    { field: 'usuarioNome', headerName: 'Usuário' },
  ];

  if (loading) {
    return (
      <Layout>
        <LoadingSpinner />
      </Layout>
    );
  }

  return (
    <Layout>
      <Box sx={{ mb: 3 }}>
        <Typography variant="h4" gutterBottom>
          Financeiro
        </Typography>
        <Typography variant="body2" color="textSecondary">
          Lançamentos financeiros gerados pelas vendas realizadas.
        </Typography>
      </Box>

      {error && (
        <Alert severity="error" onClose={() => setError('')} sx={{ mb: 2 }}>
          {error}
        </Alert>
      )}

      <DataTable
        columns={columns}
        data={lancamentos}
        actions={false}
      />
    </Layout>
  );
};

export default Financeiro;
