import React, { useState, useEffect } from 'react';
import {
  Button,
  Alert,
  Chip,
} from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import { useNavigate } from 'react-router-dom';
import Layout from '../../components/Layout/Layout';
import PageHeader from '../../components/Common/PageHeader';
import DataTable from '../../components/Common/DataTable';
import LoadingSpinner from '../../components/Common/LoadingSpinner';
import vendaService from '../../services/vendaService';
import { formatCurrency, formatDateTime } from '../../utils/formatters';

const VendasList = () => {
  const [vendas, setVendas] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    loadVendas();
  }, []);

  const loadVendas = async () => {
    try {
      const data = await vendaService.listarTodas();
      setVendas(Array.isArray(data) ? data : []);
    } catch (err) {
      setError('Erro ao carregar vendas');
      setVendas([]);
    } finally {
      setLoading(false);
    }
  };

  const columns = [
    { field: 'numeroVenda', headerName: 'Número' },
    {
      field: 'dataVenda',
      headerName: 'Data',
      render: (value) => formatDateTime(value),
    },
    { field: 'clienteNome', headerName: 'Cliente' },
    {
      field: 'total',
      headerName: 'Total',
      render: (value) => formatCurrency(value),
      align: 'right',
    },
    { field: 'formaPagamento', headerName: 'Forma de Pagamento' },
    {
      field: 'status',
      headerName: 'Status',
      render: (value) => (
        <Chip
          label={value}
          color={value === 'CONCLUIDA' ? 'success' : 'default'}
          size="small"
        />
      ),
    },
  ];

  if (loading)
    return (
      <Layout>
        <LoadingSpinner />
      </Layout>
    );

  return (
    <Layout>
      <PageHeader
        title="Vendas"
        subtitle="Histórico de vendas realizadas"
        actions={<Button variant="contained" startIcon={<AddIcon />} onClick={() => navigate('/vendas/nova')}>Nova Venda</Button>}
      />

      {error && (
        <Alert severity="error" onClose={() => setError('')} sx={{ mb: 2 }}>
          {error}
        </Alert>
      )}

      <DataTable columns={columns} data={vendas} actions={false} />
    </Layout>
  );
};

export default VendasList;
